import { PDFDocument } from 'pdf-lib';
import { Base64Encode } from 'base64-stream';
import { Buffer } from 'buffer';
import PdfPrinter from 'pdfmake';
import path from 'path';
import { fileURLToPath } from 'url';
import { logger } from 'firebase-functions/v1';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const printer = new PdfPrinter({
  Roboto: {
    normal: __dirname + '/fonts/Roboto-Regular.ttf',
    bold: __dirname + '/fonts/Roboto-Medium.ttf',
    italics: __dirname + '/fonts/Roboto-Italic.ttf',
    bolditalics: __dirname + '/fonts/Roboto-MediumItalic.ttf',
  },
});

export const getPdfAssets = async (bucket, db, metadata, userId) => {
  const { imagePaths, pdfPaths } = splitImagesFromPdfs(metadata);
  imagePaths.seal = await getSurveyorSeal(db, userId);

  const images = await getBase64Images(bucket, imagePaths);
  const pdfs = await getBinaryPdfs(bucket, pdfPaths);

  return { images, pdfs };
};

export const splitImagesFromPdfs = (paths) => {
  const image = /.(jpg|jpeg|png)$/i;
  const pdf = /.(pdf)$/i;

  const imagePaths = {};
  const pdfPaths = {};

  for (const [key, value] of Object.entries(paths)) {
    if ((value?.length ?? 0) === 0) {
      continue;
    }

    if (image.test(value)) {
      imagePaths[key] = value;

      continue;
    }

    if (pdf.test(value) && key.startsWith('extra')) {
      pdfPaths[key] = value;

      continue;
    }
  }

  return { imagePaths, pdfPaths };
};

const getSurveyorSeal = async (db, userId) => {
  let seal = '';

  try {
    const snapshot = await db.collection('submitters').doc(userId).get();

    const doc = snapshot.data();

    logger.debug('user doc', doc, { structuredData: true });

    if ((doc.seal?.length ?? 0) > 0) {
      seal = doc.seal;
    }
  } catch (error) {
    logger.error('error looking for surveyor seal', userId, error, {
      structuredData: true,
    });
  }

  return seal;
};

const getBase64Images = async (bucket, metadata) => {
  const promises = [];

  Object.entries(metadata).forEach(([key, fileName]) => {
    logger.debug('creating stream for', key, {
      structuredData: true,
    });

    promises.push(
      getImageData(bucket.file(fileName).createReadStream()).then((result) => {
        return { [key]: result };
      })
    );
  });

  const results = await Promise.all(promises);

  return results.reduce((obj, item) => {
    const [key, value] = Object.entries(item)[0];

    return Object.assign(obj, { [key]: value });
  }, {});
};

const getBinaryPdfs = async (bucket, metadata) => {
  const promises = [];

  Object.entries(metadata).forEach(([key, fileName]) => {
    logger.debug('creating stream for', key, {
      structuredData: true,
    });

    promises.push(
      getPdfData(bucket.file(fileName).createReadStream()).then((result) => {
        return { [key]: result };
      })
    );
  });

  const results = await Promise.all(promises);

  return results.reduce((obj, item) => {
    const [key, value] = Object.entries(item)[0];

    return Object.assign(obj, { [key]: value });
  }, {});
};

const getImageData = (stream) => {
  const chunks = new Base64Encode();
  let contentType = '';

  return new Promise((resolve, reject) => {
    stream.on('error', (err) => {
      logger.error('getImageData error', err, { structuredData: true });

      return reject(err);
    });
    stream.on('response', (response) => {
      contentType = response.headers['content-type'];

      return;
    });
    stream.on('data', (chunk) => chunks.write(chunk));
    stream.on('end', () => {
      chunks.end();

      return resolve(`data:${contentType};base64,${chunks.read()}`);
    });
  });
};

const getPdfData = (stream) => {
  const chunks = [];

  return new Promise((resolve, reject) => {
    stream.on('data', (chunk) => chunks.push(chunk));
    stream.on('end', () => resolve(Buffer.concat(chunks)));
    stream.on('error', (err) => {
      logger.error('getPdfData error', err, { structuredData: true });

      return reject(err);
    });
  });
};

export const createPdfDocument = async (definition, extraPdfPages) => {
  const partialPdf = await new Promise((resolve, reject) => {
    const chunks = [];
    const stream = printer.createPdfKitDocument(definition);

    stream.on('data', (chunk) => chunks.push(chunk));
    stream.on('end', () => resolve(Buffer.concat(chunks)));
    stream.on('error', (error) => reject(error));
    stream.end();
  });

  logger.debug('saved front page now appending', {
    structuredData: true,
  });

  const pdf = await appendPdfPages(partialPdf, extraPdfPages);

  return pdf;
};

export const appendPdfPages = async (original, metadata) => {
  const source = await PDFDocument.load(original);

  await Object.values(metadata).forEach(async (extra) => {
    const extraPages = await PDFDocument.load(extra);
    const pageCount = extraPages.getPageIndices();

    logger.debug('loading extra pages', pageCount.length, {
      structuredData: true,
    });

    const copiedPages = await source.copyPages(extraPages, pageCount);

    copiedPages.forEach((page, i) => {
      logger.debug('adding pages', i, {
        structuredData: true,
      });
      source.addPage(page);
    });
  });

  const complete = await source.save();

  return complete;
};

export const constants = {
  grid: ['40%', '10%', '10%', '10%', '10%', '10%', '10%'],
  half: '50%',
  sky500: '#0ea5e9',
  label: ['text-normal', 'bold', 'bg-sky-800', 'sky-50'],
  value: ['text-normal', 'slate-800'],
  subLabel: ['text-normal', 'slate-800', 'bold'],
  header: ['text-xl', 'bold', 'center', 'sky-800'],
  subHeader: ['text-lg', 'bold', 'center', 'sky-800'],
  footer: ['text-sm', 'italic', 'center', 'rose-700'],
  map: 'data:image/png;base64,',
  monument:
    'data:image/png;base64,',
  close:
    'data:image/png;base64,',
  seal: 'data:image/png;base64,',
  additional:
    'data:image/png;base64,',
};
